        // java.log("ä¸‹æ¶ä¹¦ç±ï¼Œä½¿ç”¨webæ¥å£è·å–ç›®å½•");



        //  webæ¥å£  ##å·å&æ—¶é—´
    case 'WEB':
        let Json, Host = "https://fanqienovel.com";
        if (false) {
            Json = java.ajax(Host + "/page/" + book_id)
                .match(/__INITIAL_STATE__=([^;]+)/)[1];
        } else {
            Json = java.ajax(Host + "/api/reader/directory/detail?bookId=" + book_id);
        }
        // java.log(Json)
        let List = JSON.parse(Json).data.chapterListWithVolume;
        if (List) {
          for (let list of List) {
              v_Index.push(c_Array.length);
              list.map((_, i) => {
                  let c_name = _.volume_name || "ç¬¬ä¸€å·ï¼šé»˜è®¤";
                  let c_time = java.timeFormat(_.firstPassTime * 1000);
                  if (!i) push_a(c_name, true);
                  push_a(_.title, false, _.itemId, [c_name, c_time].join(" | "))
              });
          }
          break;
        }
}



// è¿”å›æ•°ç»„
let len = v_Index.length;
// java.log(JSON.stringify(v_Index))
if (len < 2) { // åˆ†å·å¤ªå°‘ä¸æ˜¾ç¤º
    for (; len > 0; len--) {
        c_Array.splice(v_Index[len - 1], 1);
    }
} else if (false) { // æ˜¾ç¤ºæ•ˆæœä¸å¥½çœ‹
    for (let a, b, i = 0; i < len; i++) {
        a = v_Index[i];
        b = v_Index[i + 1];
        if (b == undefined) b = len;
        c_Array[a]["ChapterInfo"] = "å…± " + (b - a - 1) + " ç« ";
    }
}

